select
  id, stock_code, stock_name, plate, main_business, niche_business, circulation, instructions, yesterday_gains, start_gains, gains, amplitude, changing_hands, yesterday_amplitude, yesterday_changing_hands, average_Price10, average_Price5, current_Price, create_date, create_by, modifed_date, modifed_by, trend_type, remark
from base_bd_up_stock


        and id = #{id}


        and stock_code = #{stockCode}


        and stock_name = #{stockName}


        and plate = #{plate}


        and main_business = #{mainBusiness}


        and niche_business = #{nicheBusiness}


        and circulation = #{circulation}


        and instructions = #{instructions}


        and yesterday_gains = #{yesterdayGains}


        and start_gains = #{startGains}


        and gains = #{gains}


        and amplitude = #{amplitude}


        and changing_hands = #{changingHands}


        and yesterday_amplitude = #{yesterdayAmplitude}


        and yesterday_changing_hands = #{yesterdayChangingHands}


        and average_Price10 = #{averagePrice10}


        and average_Price5 = #{averagePrice5}


        and current_Price = #{currentPrice}


        and create_date = #{createDate}


        and create_by = #{createBy}


        and modifed_date = #{modifedDate}


        and modifed_by = #{modifedBy}


        and trend_type = #{trendType}


        and remark = #{remark}


limit #{pageable.offset}, #{pageable.pageSize}

